import React, { useState, useEffect } from 'react';

import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';
import api from './services/api';

function App() {

  const [github_username,setGithub_username]= useState('')
  const [latitude, setLatitude] = useState('')
  const [longitude, setLongitude] = useState('')
  const [techs, setTechs] = useState('')
  const [devs,setDevs] = useState([]) //starts state with same type of data will be stored

    useEffect(()=>{ 
    navigator.geolocation.getCurrentPosition(
      (position) => {
        const {latitude,longitude} = position.coords

        setLatitude(latitude)
        setLongitude(longitude)
      },
      (err)=>{
        console.log(err)
      },
      {
        timeout: 30000,
      }
    )
  },[])

  async function handleAddDev(e){
    e.preventDefault()
    github_username.trim()
    const response = await api.post('/devs',{
      github_username,techs,latitude,longitude
    })

    setGithub_username('')
    setTechs('')
  }

  useEffect(()=>{
    async function loadDevs(){
      const response = await api.get('/devs')
      setDevs(response.data)
      console.log(response.data)
    }
    loadDevs()
  },[])

   return (
    <div id="app">
      <h1>heyDev</h1>
      <div className="main-content">
        <aside>
          <strong>Cadastrar</strong>
          <form onSubmit={handleAddDev}>
            <div className="input-block">
              <label htmlFor="github_username">Usu√°rio no Github</label>
              <input 
                name="github_username" 
                id="github_username" 
                required
                onChange={e=>{setGithub_username(e.target.value)}}
                value={github_username}/>
            </div>

            <div className="input-block">
              <label htmlFor="techs">Tecnologias</label>
              <input 
                name="techs" 
                id="techs" 
                required
                onChange={e=>{setTechs(e.target.value)}}
                value={techs}/>
            </div>

            <div className="input-group">            
              <div className="input-block">
                <label htmlFor="latitude">Latitude</label>
                <input 
                  type="number" 
                  name="latitude" 
                  id="latitude" 
                  required 
                  value={latitude}
                  onChange={e => setLatitude(e.target.value)}/>
              </div>

              <div className="input-block">
                  <label htmlFor="longitude">Longitude</label>
                  <input 
                    type="number" 
                    name="longitude" 
                    id="longitude" 
                    required 
                    value={longitude}
                    onChange={e => setLongitude(e.target.value)}/>
              </div>
            </div>
            <button type="submit">Salvar</button>
          </form>
        </aside>

        <main>
          <ul>
            {devs.map(dev) }
            <li className="dev-item">
              <header>
                <img src="https://avatars0.githubusercontent.com/u/2254731?s=460&v=4" alt="Diego Fernandes"/>
                <div className="user-info">
                  <strong>Diego Fernandes</strong>
                  <span>Javascript, VueJS</span>
                </div>
              </header>
              <p>CTO at RocketSeat</p>
              <a href="https://github.com/mlasa">Ir para o perfil no Github</a>
            </li>
          </ul>
        </main>
      </div>
    </div>
  );
}

export default App;
